sec.code:
# Set CRAN mirror to avoid installation errors
options(repos = c(CRAN = "https://cloud.r-project.org"))

# Install required packages if not already installed
if (!require(leaflet, quietly = TRUE)) {
  tryCatch({
    install.packages("leaflet", dependencies = TRUE)
  }, error = function(e) {
    message("Could not install leaflet package. Interactive map may not render properly.")
  })
}
if (!require(dplyr, quietly = TRUE)) {
  tryCatch({
    install.packages("dplyr", dependencies = TRUE)
  }, error = function(e) {
    message("Could not install dplyr package. Table formatting may not work properly.")
  })
}

# Load required packages with error handling
suppressWarnings({
  library(dplyr, quietly = TRUE)

  # Try to load leaflet, but don't fail if it's not available
  leaflet_available <- tryCatch({
    library(leaflet, quietly = TRUE)
    TRUE
  }, error = function(e) {
    FALSE
  })

  if (!leaflet_available) {
    message("leaflet package not available. Interactive map will be replaced with a static map.")
  }
})
